mapper.select:

SELECT * FROM (
  SELECT ROWNUM AS RNUM, SUBQUERY.*
  FROM (
    SELECT COMMENT_NUMBER,P.POST_NUMBER,MEMBER_NUMBER,COMMENT_CONTENT,COMMENTED_DATE,COMMENT_DELETE_STATE
    FROM TBL_COMMENT
    WHERE MEMBER_NUMBER = #{memberNumber}
      AND COMMENT_DELETE_STATE = 'N'
    ORDER BY COMMENTED_DATE DESC
  ) SUBQUERY
)
