mapper.select:
SELECT * FROM ( SELECT ROWNUM AS rowNumber, A.* FROM ( SELECT p.POST_NUMBER AS postNumber, p.POST_TITLE AS postTitle, TO_CHAR(p.POST_CREATED_DATE, 'YYYY-MM-DD') AS postCreatedDate, TO_CHAR(p.POST_UPDATED_DATE, 'YYYY-MM-DD') AS postUpdatedDate, p.POST_TYPE AS postType, n.NOTICE_CONTENT AS noticeContent, a.ADMIN_NUMBER AS adminNumber, a.ADMIN_ID AS adminId, a.ADMIN_TREE_GRADE AS adminTreeGrade FROM TBL_POST p JOIN TBL_NOTICE n ON n.POST_NUMBER = p.POST_NUMBER JOIN TBL_ADMIN a ON a.ADMIN_NUMBER = p.ADMIN_NUMBER WHERE p.POST_TYPE = 'NOTICE'
AND p.POST_TITLE LIKE '%' || #{searchWord} || '%'
ORDER BY p.POST_NUMBER DESC ) A WHERE ROWNUM <= #{endRow} ) WHERE rowNumber >= #{startRow}

mapper.update:
UPDATE TBL_NOTICE
SET NOTICE_CONTENT = #{noticeContent}
WHERE POST_NUMBER = #{postNumber}

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS rowNumber, A.* FROM ( SELECT p.POST_NUMBER AS postNumber, p.POST_TITLE AS postTitle, TO_CHAR(p.POST_CREATED_DATE, 'YYYY-MM-DD') AS postCreatedDate, TO_CHAR(p.POST_UPDATED_DATE, 'YYYY-MM-DD') AS postUpdatedDate, p.POST_TYPE AS postType, f.FAQ_CONTENT AS faqContent, a.ADMIN_NUMBER AS adminNumber, a.ADMIN_ID AS adminId, a.ADMIN_TREE_GRADE AS adminTreeGrade FROM TBL_POST p JOIN TBL_FAQ f ON f.POST_NUMBER = p.POST_NUMBER JOIN TBL_ADMIN a ON a.MEMBER_NUMBER = p.MEMBER_NUMBER WHERE p.POST_TYPE = 'FAQ'
AND p.POST_TITLE LIKE '%' || #{searchWord} || '%'
ORDER BY p.POST_NUMBER DESC ) A WHERE ROWNUM <= #{endRow} ) WHERE rowNumber >= #{startRow}

mapper.update:
UPDATE TBL_FAQ
SET FAQ_CONTENT = #{faqContent}
WHERE
POST_NUMBER = #{postNumber}

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS rowNumber, A.* FROM ( SELECT p.POST_NUMBER AS postNumber, p.POST_TITLE AS postTitle, TO_CHAR(p.POST_CREATED_DATE,'YYYY-MM-DD') AS postCreatedDate, TO_CHAR(p.POST_UPDATED_DATE,'YYYY-MM-DD') AS postUpdatedDate, p.POST_TYPE AS postType, i.INQUIRY_CONTENT AS inquiryContent, i.INQUIRY_STATUS AS inquiryStatus, m.MEMBER_NUMBER AS memberNumber, m.MEMBER_ID AS memberId, CASE m.MEMBER_TYPE WHEN 'GENERAL' THEN g.GENERAL_NAME WHEN 'SELLER' THEN s.SELLER_NAME END AS memberName FROM TBL_POST p JOIN TBL_INQUIRY i ON i.POST_NUMBER = p.POST_NUMBER JOIN TBL_MEMBER m ON m.MEMBER_NUMBER = p.MEMBER_NUMBER LEFT JOIN TBL_GENERAL_MEMBER g ON g.MEMBER_NUMBER = m.MEMBER_NUMBER LEFT JOIN TBL_SELLER_MEMBER s ON s.MEMBER_NUMBER = m.MEMBER_NUMBER WHERE p.POST_TYPE = 'INQUIRY'
AND p.POST_TITLE LIKE '%' || #{searchWord} || '%'
ORDER BY p.POST_NUMBER DESC ) A WHERE ROWNUM <= #{endRow} ) WHERE rowNumber >= #{startRow}

mapper.update:
UPDATE TBL_INQUIRY
SET INQUIRY_STATUS = #{inquiryStatus}
WHERE POST_NUMBER = #{postNumber}

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS rowNumber, A.* FROM ( SELECT m.MEMBER_NUMBER AS memberNumber, m.MEMBER_TYPE AS memberType, m.MEMBER_ID AS memberId, CASE m.MEMBER_TYPE WHEN 'GENERAL' THEN g.GENERAL_NAME WHEN 'SELLER' THEN s.SELLER_NAME ELSE m.MEMBER_ID END AS memberName, COALESCE(g.GENERAL_WARNING_COUNT, s.SELLER_WARNING_COUNT,0) AS warningCount, COALESCE(g.GENERAL_TREE_GRADE, s.SELLER_TREE_GRADE,'') AS treeGrade, COALESCE(g.GENERAL_PAYMENT_AMOUNT, s.SELLER_PAYMENT_AMOUNT, 0) AS paymentAmount FROM TBL_MEMBER m LEFT JOIN TBL_GENERAL_MEMBER g ON g.MEMBER_NUMBER = m.MEMBER_NUMBER LEFT JOIN TBL_SELLER_MEMBER s ON s.MEMBER_NUMBER = m.MEMBER_NUMBER
m.MEMBER_TYPE = #{memberType}
AND ( m.MEMBER_ID LIKE '%' || #{searchWord} || '%' OR (CASE m.MEMBER_TYPE WHEN 'GENERAL' THEN g.GENERAL_NAME WHEN 'SELLER' THEN s.SELLER_NAME ELSE m.MEMBER_ID END) LIKE '%' || #{searchWord} || '%' )
ORDER BY m.MEMBER_NUMBER DESC ) A WHERE ROWNUM <= #{endRow} ) WHERE rowNumber >= #{startRow}

SELECT COUNT(*) FROM TBL_MEMBER m LEFT JOIN TBL_GENERAL_MEMBER g ON g.MEMBER_NUMBER = m.MEMBER_NUMBER LEFT JOIN TBL_SELLER_MEMBER s ON s.MEMBER_NUMBER = m.MEMBER_NUMBER
m.MEMBER_TYPE = #{memberType}
AND ( m.MEMBER_ID LIKE '%' || #{searchWord} || '%' OR (CASE m.MEMBER_TYPE WHEN 'GENERAL' THEN g.GENERAL_NAME WHEN 'SELLER' THEN s.SELLER_NAME ELSE m.MEMBER_ID END) LIKE '%' || #{searchWord} || '%' )
