mapper.select:

	SELECT*FROM(
		SELECT ROWNUM AS RNUM,SUBQUERY.*
		FROM(
		SELECT M.MEMBER_NUMBER,M.MEMBER_ID,P.POST_NUMBER, P.POST Title,P.BOARD_CONTENT,P.POST_CREATED_DATE,P.POST_VIEW_COUNT
		FROM TBL_MEMBER M JOIN TBL_POST P
		ON M.MEMBER_NUMBER=P.MEMBER_NUMBER
		ORDER BY P.POST_CREATED_DATE DESC
	)SUBQUERY
)WHERE RNUM BETWEEN #{startRow} AND #{endRow}

mapper.update:
UPDATE TBL_POST
SET POST_READ_COUNT=POST_READ_COUNT+1
WHERE POST_NUMBER=#{postNumber}

UPDATE TBL_POST
SET POST_TITLE=#{postTitle}, FREE_CONTENT=#{freeContent},PROMO_CONTENT=#{promoContent},RECIPE_CONTENT=#{recipeContent}

WHERE POST_NUMBER=#{postNumber}
