mapper.delete:
DELETE FROM tbl_item
where
item_number = #{itemNumber}
and item_type = 'FOOD';

DELETE FROM tbl_item
where item_number = #{itemNumber}
and item_type = 'INGREDIENT'

mapper.select:
select count(item_number) from tbl_item
where item_name =
#{itemName} and item_type = 'FOOD' and business_number = #{businessNumber}
